import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Alert from '../components/Alert';
import '../components/signin.css';
import data from '../data.json';

const path = data.backend;

function SignUp() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [status, setStatus] = useState('');
  const [success, setSuccess] = useState('');
  const [usernameAvailable, setUsernameAvailable] = useState(null);
  const [passwordStrength, setPasswordStrength] = useState('');
  const [passwordMatch, setPasswordMatch] = useState(null);

  useEffect(() => {
    if (username) {
      const checkUsernameAvailability = async () => {
        try {
          const response = await axios.get(`${path}/signup/check-username`, { params: { username } });
          setUsernameAvailable(response.data.available);
        } catch (error) {
          setUsernameAvailable(null);
        }
      };
      const delayDebounceFn = setTimeout(() => {
        checkUsernameAvailability();
      }, 500);
      return () => clearTimeout(delayDebounceFn);
    }
  }, [username]);

  useEffect(() => {
    const checkPasswordStrength = (password) => {
      if (!password) return '';
      if (password.length < 6) return 'Weak';
      if (/[A-Z]/.test(password) && /[0-9]/.test(password) && /[^A-Za-z0-9]/.test(password)) {
        return 'Strong';
      }
      return 'Medium';
    };
    setPasswordStrength(checkPasswordStrength(password));
  }, [password]);

  useEffect(() => {
    setPasswordMatch(confirmPassword === password && confirmPassword !== '');
  }, [password, confirmPassword]);

  function handleSubmit(event) {
    event.preventDefault();
    if (!username || !password || !confirmPassword) {
      setStatus('Please fill in all fields.');
      setSuccess(false);
      return;
    }
    if (password !== confirmPassword) {
      setStatus('Passwords do not match.');
      setSuccess(false);
      return;
    }
    if (usernameAvailable === false) {
      setStatus('Username is already taken.');
      setSuccess(false);
      return;
    }
    axios.post(`${path}/signup`, { username, password })
      .then(res => {
        setStatus(res.data.status);
        setSuccess(res.data.success);
        if (res.data.success) {
          localStorage.setItem('token', res.data.token);
          window.location.href = '/home';
        }
      })
      .catch(() => {
        setStatus('An error occurred. Please try again.');
        setSuccess(false);
      });
  }

  const isFormValid =
    username &&
    password &&
    confirmPassword &&
    password === confirmPassword &&
    usernameAvailable === true &&
    passwordStrength === 'Strong';

  return (
    <div className="wrapper">
      <form onSubmit={handleSubmit}>
        <h2>Sign Up</h2>
        <Alert message={status} success={success} />

        <div className="input-field">
          <input
            type="text"
            required
            onChange={(e) => setUsername(e.target.value)}
          />
          <label>Enter your username</label>
          {username && (usernameAvailable === null ? <small>Checking...</small> : usernameAvailable ? <small className="text-success">Username is available</small> : <small className="text-danger">Username is taken</small>)}
        </div>

        <div className="input-field">
          <input
            type="password"
            required
            onChange={(e) => setPassword(e.target.value)}
          />
          <label>Enter your password</label>
          {password && <small className={`text-${passwordStrength === 'Strong' ? 'success' : passwordStrength === 'Medium' ? 'warning' : 'danger'}`}>Strength: {passwordStrength}</small>}
        </div>

        <div className="input-field">
          <input
            type="password"
            required
            onChange={(e) => setConfirmPassword(e.target.value)}
          />
          <label>Confirm your password</label>
          {confirmPassword && (passwordMatch ? <small className="text-success">Passwords match</small> : <small className="text-danger">Passwords do not match</small>)}
        </div>

        <div className="forget">
          <label>
            <input type="checkbox" id="remember" />
            <p>Remember me</p>
          </label>
        </div>

        <button type="submit" disabled={!isFormValid}>Sign Up</button>

        <div className="register">
          <p>Already have an account? <a href="/signin">Sign In</a></p>
        </div>
      </form>
    </div>
  );
}

export default SignUp;
