import React, { useState } from 'react';
import axios from 'axios';
import Alert from '../components/Alert';
import data from '../data.json';

const path = data.backend;

function SignIn() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [status, setStatus] = useState('');
  const [success, setSuccess] = useState('');

  function handleSubmit(event) {
    event.preventDefault();

    if (!username || !password) {
        setStatus('Please fill in both fields.');
        setSuccess(false);
        return;
    }

    axios.post(`${path}/signin`, { username, password })
        .then(res => {
            setStatus(res.data.status);
            setSuccess(res.data.success);
            if (res.data.success) {
                localStorage.setItem('token', res.data.token);
                window.location.href = '/home';
            }
        })
        .catch(err => {
            console.log(err);
            setStatus('An error occurred. Please try again.');
            setSuccess(false);
        });
  }

  return (
    <div className="container d-flex align-items-center justify-content-center vh-100">
      <div className="card shadow-lg p-5" style={{ width: '100%', maxWidth: '400px', borderRadius: '10px' }}>
        <h3 className="text-center mb-4 text-primary font-weight-bold">Sign In</h3>
        <form onSubmit={handleSubmit}>
          <Alert message={status} success={success} />

          <div className="mb-4">
            <label htmlFor="username" className="form-label">Username</label>
            <input
              type="text"
              className="form-control"
              id="username"
              placeholder="Enter your username"
              onChange={(e) => setUsername(e.target.value)}
              style={{ borderRadius: '10px', padding: '10px' }}
            />
          </div>

          <div className="mb-4">
            <label htmlFor="password" className="form-label">Password</label>
            <input
              type="password"
              className="form-control"
              id="password"
              placeholder="Enter your password"
              onChange={(e) => setPassword(e.target.value)}
              style={{ borderRadius: '10px', padding: '10px' }}
            />
          </div>

          <div className="d-grid">
            <button 
              type="submit" 
              className="btn btn-primary btn-lg" 
              style={{ borderRadius: '10px', padding: '12px' }}
            >
              Sign In
            </button>
          </div>
        </form>

        <div className="text-center mt-4">
          <a href="/signup" className="text-muted">Don't have an account? Sign Up</a>
        </div>
      </div>
    </div>
  );
}

export default SignIn;
